import React, {
  createContext,
  useReducer,
  useState,
  useContext,
  useEffect,
} from "react";
import Reducer from "./Reducer";
import db, { auth, provider } from "../firebase";

export const initialState = {
  user: null,
};

export const Context = createContext(initialState); //GlobalContext

export const ContextProvider = (props) => {
  //Global Provider
  const [state, dispatch] = useReducer(Reducer, initialState);

  const signIn = () => {
    auth
      .signInWithPopup(provider)
      .then((result) => {
        dispatch({
          type: "set__user",
          payload: result.user,
        });
        //console.log(result.user)
      })
      .catch((error) => alert(error.message));
  };

  const [posts, setPosts] = useState([]);

  useEffect(() => {
    db.collection("posts")
      .orderBy("timestamp", "desc")
      .onSnapshot((snapshot) =>
        setPosts(
          snapshot.docs.map((doc) => {
            return {
              id: doc.id,
              data: doc.data(),
            };
          })
        )
      );
  }, []);



  const addPost = (post) => {
    dispatch({ type: "add__post", payload: post });
  };

  return (
    <Context.Provider
      value={{ signIn: signIn, user: state.user, posts: posts }}
    >
      {props.children}
    </Context.Provider>
  );
};
