/* eslint-disable import/no-anonymous-default-export */
export default (state, action) => {
  switch (action.type) {
    case "set__user":
      return {
        ...state,
        user: action.payload,
      };
      case "add__post":
          return 
          // eslint-disable-next-line no-labels
          posts: [
            ...state,
            {
              id: action.posts.id,
              content: action.posts.content,
              profilePic: action.profilePic,
            },
          ];

          
    default:
      return state;
  }
};
