/* eslint-disable import/no-anonymous-default-export */
export default (state, action) => {
  switch (action.type) {
    case "set__user":
      return {
        ...state,
        user: action.payload,
      };
    case "add__post":
      return {
        ...state,
        posts: [
          {
            title: action.content.title,
            text: action.content.text,
            
          },
        ],
      };
    default:
      return state;
  }
};
