import { ContextProvider, Context } from "../statemanagement/Context";
import React, { useContext } from "react";
import "../styles/UserCard.css";
import Avatar from "@material-ui/core/Avatar";
import LocationOnIcon from "@material-ui/icons/LocationOn";

function UserCard() {

  const {user} = useContext(Context);

  return (
    <div className="usercard">
      <div className="usercard__main">
        <div className="usercard__upper">
          <Avatar
            src={user.photoURL}
            style={{ height: "6.5vh", width: "6.5vh" }}
          />
          <h2>{user.displayName}</h2>
          <span>@{user.displayName.toLowerCase().join}</span>
          <div>
            <LocationOnIcon style={{ color: "#8c9096", height: "1.9vh" }} />
            <span>Somewhere, Turkey</span>
          </div>
        </div>
        <div className="usercard__info">
          <div>
            Posts<span>18</span>
          </div>
          <div>
            Followers <span>45</span>
          </div>
          <div>
            Following <span>47</span>
          </div>
        </div>
      </div>

      <div className="usercard__actions">
        <div></div>
        <div></div>
      </div>
    </div>
  );
}

export default UserCard;
