import React, { createContext, useReducer, useState, useContext } from "react";
import Reducer from "./Reducer";
import firebase, { auth, provider } from "../firebase";

export const initialState = {
  posts: [],
  user: null,
};

export const Context = createContext(initialState); //GlobalContext

export const ContextProvider = (props) => {
  //Global Provider
  const [state, dispatch] = useReducer(Reducer, initialState);
  const addtoBasket = (product) => {
    dispatch({ type: "add__to__basket", payload: product });
  };
  const removefromBasket = (id) => {
    dispatch({ type: "remove__from__basket", payload: id });
  };
  const signIn = () => {
    auth
      .signInWithPopup(provider)
      .then((result) => {
        dispatch({
          type: "set__user",
          payload: result.user,
        });
        //console.log(result.user)
      })
      .catch((error) => alert(error.message));
  };
  const [value, setValue] = useState("xxxXXxx");

  const handleChange = (e) => {
    setValue(e.toLowerCase());
  };



  return (
    <Context.Provider value={{ signIn: signIn }}>
      {props.children}
    </Context.Provider>
  );
};
