import { ContextProvider, Context } from "../statemanagement/Context";
import React, { useContext } from "react";
import "../styles/Post.css";
import Avatar from "@material-ui/core/Avatar";
import ThumbUpAltRoundedIcon from "@material-ui/icons/ThumbUpAltRounded";
import ChatRoundedIcon from "@material-ui/icons/ChatRounded";
import ShareRoundedIcon from "@material-ui/icons/ShareRounded";
import db from "../firebase"
import firebase from "../firebase"
import { requirePropFactory } from "@material-ui/core";

const Post = (props) => {

  const deletePost = () => {
    //alert("I am going to delete that shit m8");
    db.collection("posts").doc("id").delete()
      
      .then(() => {
        console.log("successfully deleted! ");
      })
      .catch((error) => {
        console.log("Error removing document:", error);
      });
    }
    console.log(props.content)
  
 


  return (
    <div className="post" >
      <div className="post__info">
        <Avatar src={props.profilePic} />
        <span>{new Date(props.timestamp?.toDate()).toUTCString()}</span>
      </div>
      <p className="post__content">{props.content}</p>
      <div className="post__icons">
        <div>
          <ThumbUpAltRoundedIcon
            style={{ color: "#0197d2", marginRight: "10px" }}
          />
          <span>Like</span>
        </div>
        <div>
          <ChatRoundedIcon style={{ color: "#0197d2", marginRight: "10px" }} />
          <span>Comment</span>
        </div>
        <div>
          <ShareRoundedIcon onClick = {deletePost} style={{ cursor :"pointer", color: "#0197d2", marginRight: "10px" }} />
          <span>Share</span>
        </div>
      </div>
    </div>
  );
};

export default Post;
